export const CodeBox = () => {
    const [copied, setCopied] = useState(false);

    const handleCopy = async () => {
        try {
            await navigator.clipboard.writeText(SPY_SCRIPT);
            setCopied(true);
            toast.success("Script copiado al portapapeles!", {
                description: "Ahora pgalo en la consola (F12).",
                style: {
                    background: "rgba(10, 10, 10, 0.9)",
                    border: "1px solid #7c3aed",
                    color: "#fff"
                }
            });
            setTimeout(() => setCopied(false), 2000);
        } catch (err) {
            toast.error("Error al copiar el script.");
        }
    };

    return (
        <motion.div
            initial={{ scale: 0.95, opacity: 0 }}
            whileInView={{ scale: 1, opacity: 1 }}
            viewport={{ once: true }}
            className="w-full max-w-3xl mx-auto mb-16 px-4"
        >
            <div className="relative group rounded-xl overflow-hidden border border-white/10 bg-black/50 backdrop-blur-xl shadow-2xl shadow-neon-purple/10">

                {/* Header of the code box */}
                <div className="flex items-center justify-between px-4 py-3 bg-white/5 border-b border-white/5">
                    <div className="flex items-center gap-2">
                        <div className="flex gap-1.5">
                            <div className="w-3 h-3 rounded-full bg-red-500/50" />
                            <div className="w-3 h-3 rounded-full bg-yellow-500/50" />
                            <div className="w-3 h-3 rounded-full bg-green-500/50" />
                        </div>
                        <span className="ml-3 text-xs text-white/40 font-mono flex items-center gap-1">
                            <Terminal size={12} />
                            spy-script.js
                        </span>
                    </div>

                    <button
                        onClick={handleCopy}
                        className="flex items-center gap-2 px-3 py-1.5 text-xs font-medium text-white/70 bg-white/5 hover:bg-white/10 hover:text-white rounded-md transition-all border border-white/5 group-hover:border-neon-purple/30"
                    >
                        {copied ? (
                            <>
                                <Check size={14} className="text-green-400" />
                                <span className="text-green-400">Copiado!</span>
                            </>
                        ) : (
                            <>
                                <Copy size={14} />
                                <span>Copiar Cdigo</span>
                            </>
                        )}
                    </button>
                </div>

                {/* Code Content */}
                <div className="p-4 overflow-x-hidden overflow-y-auto h-[350px] flex items-stretch bg-[#0c0c0c] custom-scrollbar">
                    <pre className="font-mono text-sm leading-relaxed text-blue-100/80 w-full">
                        <code className="block">
                            <div className="table-row">
                                <span className="table-cell text-right pr-4 select-none text-white/20 w-8 border-r border-white/5 bg-white/5">1</span>
                                <span className="table-cell pl-4 break-all whitespace-pre-wrap">{SPY_SCRIPT}</span>
                            </div>
                            {/* Fake lines to add height/context feeling */}
                            {Array.from({ length: 15 }).map((_, i) => (
                                <div key={i} className="table-row opacity-20 select-none">
                                    <span className="table-cell text-right pr-4 text-white/10 w-8 border-r border-white/5 bg-white/5">{i + 2}</span>
                                    <span className="table-cell pl-4">~</span>
                                </div>
                            ))}
                        </code>
                    </pre>
                </div>

                {/* Glow effect */}
                <div className="absolute inset-0 pointer-events-none bg-radial-gradient from-neon-purple/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
            </div>
        </motion.div>
    );
};
